func createPost(author string, body string, time time.Time, channel string, tree *graviton.Tree) {
	post := Message{author, body, time}
	postByte := msgEncode(post)
	tree.Put([]byte(channel), postByte)
}

func userEncode(u User) []byte {
	var network bytes.Buffer
	enc := gob.NewEncoder(&network)
	err := enc.Encode(u)
	if err != nil {
		log.Fatal("encode:", err)
	}
	return network.Bytes()
}

func userDecode(data []byte) User {
	r := bytes.NewReader(data)
	var user User
	err := gob.NewDecoder(r).Decode(&user)
	if err != nil {
		fmt.Println("Decode:", err)
	}
	return user
}

func msgEncode(m Message) []byte {
	var network bytes.Buffer
	enc := gob.NewEncoder(&network)
	err := enc.Encode(m)
	if err != nil {
		log.Fatal("encode:", err)
	}
	return network.Bytes()
}

func msgDecode(data []byte) Message {
	r := bytes.NewReader(data)
	var msg Message
	err := gob.NewDecoder(r).Decode(&msg)
	if err != nil {
		fmt.Println("Decode:", err)
	}
	return msg
}

func chanEncode(c Channel) []byte {
	var network bytes.Buffer
	enc := gob.NewEncoder(&network)
	err := enc.Encode(c)
	if err != nil {
		log.Fatal("encode:", err)
	}
	return network.Bytes()
}

func chanDecode(data []byte) Channel {
	r := bytes.NewReader(data)
	var ch Channel
	err := gob.NewDecoder(r).Decode(&ch)
	if err != nil {
		fmt.Println("Decode:", err)
	}
	return ch
}

func treePut(name string, data []byte, t *graviton.Tree) {
	err := t.Put([]byte(name), data)
	if err != nil {
		log.Fatal("tree put error:", err)
	}
}

func treeGet(name string, t *graviton.Tree) []byte {
	data, err := t.Get([]byte(name))
	if err != nil {
		log.Fatal("tree get error:", err)
	}
	return data
}
